package 제어문문제_2;

import java.util.Scanner;

public class 이번문제 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
        
        System.out.println("======1~100 사이의 8의배수======");
        int i=1;
        while(i<=100)
        {
        	if(i%8==0)
        	System.out.println(i);
        	i++;
        		
        }
        
        
       
	}

}
